import React, { Component } from "react"
import "./chat-header.css"

class ChatHeader extends Component {
    constructor() {
        super()
        this.state = {
            showform: false,
            username: "username",
        }
    }

    handleUsername = (event) => {
        this.setState({ username: event.target.value })
    }

    handleAdd = () => {
        this.setState({ showform: true })
    }

    handleSubmit = () => {
        this.props.handleAdd(this.state.username)
    }

    handleClick = () => {
        this.setState({ username: "" })
    }

    getForm = () => {
        return (
            <form onSubmit={this.handleAdd} className="header-form">
                <input
                    type="text"
                    name="username"
                    value={this.state.username}
                    onChange={this.handleUsername}
                    onClick={this.handleClick}
                    className="header-user"
                />
                <input type="submit" value="Join" className="header-join" />
            </form>
        )
    }

    render() {
        let headerForm
        if (this.state.showform === true) headerForm = this.getForm()

        return (
            <div className="header-section">
                {headerForm}
                <button onClick={this.handleAdd} className="friend">
                    Friend
                </button>
                <button onClick={this.handleAdd} className="group">
                    Group
                </button>
                <button onClick={this.handleLogout} className="logout">
                    Logout
                </button>
            </div>
        )
    }
}

export default ChatHeader
