import React, { Component } from "react"
import Message from "./message"

class PersonalMessage extends Component {
    render() {
        let className = "personal-message-" + this.props.owner
        if (this.props.type === "Text") {
            return Message.textContent(this.props.content, className + "-text")
        } else if (this.props.type === "mage") {
            return Message.imageContent(
                this.props.content,
                className + "-image"
            )
        }
    }
}

export default PersonalMessage
