import React, { Component } from "react"
import ChatHeader from "./chat-header"
import ChatList from "./chat-list"
import "./chat.css"
import Display from "./display"
import SendBar from "./sendbar"

class Chat extends Component {
    constructor(props) {
        super(props)
        this.storage = new Map()
        this.state = {
            receiverlist: new Map(),
            receiver: {
                username: "",
                type: "",
            },
            currentMessages: [],
        }
        this.username = this.props.username
    }

    handleDelete = (receiver) => {}

    handleDisplay = (receiver) => {
        console.log(receiver)
        this.setState({
            receiver: receiver,
        })

        if (this.storage.has(receivername)) {
            this.setState({
                currentMessages: this.storage.get(receivername),
            })
            this.storage.set(receivername, [
                ...this.storage.get(receivername),
                newMessage,
            ])
        } else {
            this.storage.set(receivername, [newMessage])
        }
    }

    handleLogout = () => {}

    handleUser = (u, t) => {
        if (this.state.receiverlist.has(u) === false) {
            this.setState((prevstate) => ({
                receiverlist: prevstate.receiverlist.set(u, t),
            }))
        }
    }

    handleSend = (messageBody) => {
        const receivername = this.state.receiver.username

        const newMessage = {
            from: this.username,
            to: receivername,
            type: messageBody.type,
            content: messageBody.content,
        }

        this.setState({
            currentMessages: [...this.state.currentMessages, newMessage],
        })

        if (this.storage.has(receivername)) {
            this.storage.set(receivername, [
                ...this.storage.get(receivername),
                newMessage,
            ])
        } else {
            this.storage.set(receivername, [newMessage])
        }
    }

    render() {
        return (
            <div className="container">
                <ChatHeader
                    handleUser={this.handleUser}
                    handleLogout={this.handleLogout}
                />
                <div className="section-2">
                    <ChatList
                        receivers={this.state.receiverlist}
                        onDelete={this.handleDelete}
                        onDisplay={this.handleDisplay}
                    />
                    <Display
                        username={this.username}
                        name={this.state.receiver.username}
                        messages={this.state.currentMessages}
                    />
                    <SendBar handleSend={this.handleSend} />
                </div>
            </div>
        )
    }
}

export default Chat
