import React, { Component } from "react"
import "./chat-header.css"

class ChatHeader extends Component {
    constructor() {
        super()
        this.state = {
            showform: false,
            username: "username"
        }
    }

    handleAdd = (event) => {
        this.props.handleAdd(event.target.value)
    }

    getForm = () => {
        return <form>
            <input type="text" name="username" value={this.username} onChange={this.handleUsername}>

            </input>
        </form>
    }

    render() {
        return (
            <div className="header-section">
                <button onClick={this.handleAdd} className="friend">
                    Friend
                </button>
                <button onClick={this.handleAdd} className="group">
                    Group
                </button>
                <button onClick={this.handleLogout} className="logout">
                    Logout
                </button>
            </div>
        )
    }
}

export default ChatHeader
