import React, { Component } from "react"
import ChatHeader from "./chat-header"
import ChatList from "./chat-list"
import "./chat.css"
import SendBar from "./sendbar"

class Chat extends Component {
    constructor() {
        super()
        this.storage = new Map()
        this.username = this.props.username

        this.state = {
            userlist: new Map(),
            receiver: {
                username: "",
                type: "",
            },
            currentMessages: [],
        }
    }

    handleDelete = (user) => {}
    handleDisplay = (user) => {}
    handleLogout = () => {}

    handleUser = (u, t) => {
        if (this.state.userlist.has(u) === false) {
            this.setState((prevstate) => ({
                userlist: prevstate.userlist.set(u, t),
            }))
        }
        console.log(this.state.userlist)
    }

    handleSend = (messageBody) => {
        const receivername = this.state.receiver.username

        newMessage = {
            from: this.username,
            to: receivername,
            content: messageBody,
        }

        this.setState({
            currentMessages: [...this.state.currentMessages, newMessage],
        })

        this.storage.set(receivername, [
            ...this.storage.get(receivername),
            newMessage,
        ])
    }

    render() {
        return (
            <div className="container">
                <ChatHeader
                    handleUser={this.handleUser}
                    handleLogout={this.handleLogout}
                />
                <div className="section-2">
                    <ChatList
                        users={this.state.userlist}
                        onDelete={this.handleDelete}
                        onDisplay={this.handleDisplay}
                    />
                    <SendBar />
                </div>
            </div>
        )
    }
}

export default Chat
