import React, { Component } from "react"
import Message from "./message"

class GroupMessage extends Component {
    render() {
        let header,
            innerHTML,
            className = "group-message-"

        if (this.props.owner !== "self") {
            header = <p className="group-message-header">{this.props.owner}</p>
            className += "other"
        } else {
            className += "self"
        }

        if (this.props.content === "text") {
            innerHTML = Message.textContent(
                this.props.content,
                className + "-text"
            )
        } else if (this.props.content === "image") {
            innerHTML = Message.imageContent(
                this.props.content,
                className + "-image"
            )
        }

        return (
            <div>
                {header}
                {innerHTML}
            </div>
        )
    }
}

export default GroupMessage
