import React, { Component } from "react"
import PersonalMessage from "./personal-message"
import GroupMessage from "./group-message"
import "./display.css"

class Display extends Component {
    constructor(props) {
        super(props)
        this.username = this.props.username
        this.endref = React.createRef()
    }

    componentDidUpdate() {
        this.endref.current.scrollIntoView({ behaviour: "smooth" })
    }

    groupMessages = (messages) => {
        return messages.map((message) => {
            let owner

            if (message.from === this.username) {
                owner = "self"
            } else {
                owner = message.from
            }

            return (
                <GroupMessage
                    owner={owner}
                    type={message.type}
                    content={message.content}
                />
            )
        })
    }

    personalMessages = (messages) => {
        return messages.map((message) => {
            let owner

            if (message.from === this.username) {
                owner = "self"
            } else {
                owner = "other"
            }

            return (
                <PersonalMessage
                    owner={owner}
                    type={message.type}
                    content={message.content}
                />
            )
        })
    }

    executeScroll = () => this.myRef.current.scrollIntoView()

    render() {
        let messagelist

        if (this.props.type === "group") {
            messagelist = this.groupMessages
        } else {
            messagelist = this.personalMessages
        }

        console.log(this.props.messages)

        return (
            <div className="display">
                <div className="display-header">
                    <h>{this.props.name}</h>
                </div>
                <div className="overflow">
                    <div className="display-messages">
                        {messagelist(this.props.messages)}
                    </div>
                    <div ref={this.endref} />
                </div>
            </div>
        )
    }
}

export default Display
