import React, { Component } from "react"
import "./senda"

class SendBar extends Component {
    constructor() {
        super()
        this.state = {
            type: "text",
            content: "",
        }
    }

    onSubmit = (event) => {
        event.preventDefault()
        this.props.handleSend({
            type: this.state.type,
            content: this.state.content,
        })
    }

    handleOptionClick = (event) => {
        if (this.state.type === "text") {
            this.setState({ type: "image" })
        } else {
            this.setState({ type: "text" })
        }
    }

    handleTextContent = (event) => {
        this.setState({ content: event.target.value })
    }

    getTextForm = () => {
        return (
            <form onSubmit={this.onSubmit}>
                <input
                    type="text"
                    name="image"
                    onChange={this.handleTextContent}
                />
                <input type="submit" value="Send" />
            </form>
        )
    }

    handleImageFile = (event) => {
        const reader = new FileReader()

        reader.onloadend = () => {
            this.setState({ content: reader.result })
        }

        reader.readAsDataURL(event.target.files[0])
    }

    getImgForm = () => {
        return (
            <form onSubmit={this.onSubmit}>
                <input
                    type="file"
                    name="image"
                    onChange={this.handleImageFile}
                />
                <input type="submit" value="Send" />
            </form>
        )
    }

    render() {
        let sendform

        if (this.state.type === "text") {
            sendform = this.getTextForm()
        } else if (this.state.type === "image") {
            sendform = this.getImgForm()
        }

        return (
            <div className="sendbar">
                <button onClick={this.handleOptionClick}>
                    {this.state.type}
                </button>
                {sendform}
            </div>
        )
    }
}

export default SendBar
